import React, { useEffect, useState } from 'react'
import { Link } from '@reach/router'
import axios from 'axios';
// import AuthorForm from '../components/AuthorForm'
// import AuthorList from '../components/AuthorList';

// eslint-disable-next-line
export default () => {
    const [ authors, setAuthors ] = useState([]);
    // const [loaded, setLoaded] = useState(false);
    //Create an array to store errors from the API
    // const [errors, setErrors] = useState([]); 


    useEffect(()=>{
        axios.get("http://localhost:8001/api/authors")
            // .then(res=>setProducts(res.data.products)) if res.json({products: allProducts}) instead of getting it as an array from controller
            .then(res=>{
                setAuthors(res.data)
                // setLoaded(true);
            })  
            .catch(err=>console.log("Error: ", err))     
    }, [])

    // const removeFromDom = authorId => {
    //     setAuthors(authors.filter(auth => auth._id !== authorId));
    //     console.log(authors.filter(auth => auth._id !== authorId));
    // }

    return (

        <div className="mainPage">
            <h1>Favorite Authors</h1>

            <h5><Link to='/new'> Add an author </Link></h5>
            <h4>We have quotes by:</h4>

            <table class="table table-bordered">
            <thead>
                <tr>
                <th className="firstColumn" scope="col">Author</th>
                <th className="secondColumn" scope="col">Actions available</th>
                </tr>
            </thead>
            <tbody>

                {authors.map((value, idx)=>{
                return <tr><td className="authorName" key={idx}> 
                    {value.name}
                    </td>
                <td>
                    <button type="button" className="btn btn-outline-success">
                    <Link to={`/edit/${value._id}`}>
                     <span style={{"color": "green"}}>Edit</span>
                    </Link> 

                    </button>

                    <button type="button" className="btn btn-outline-warning">Delete</button>
                </td>
                </tr>
                })}
{/* 
                <tr>
                <td>NOT FROM DB: Cheryl Strayed</td>
                <td>
                    <button type="button" class="btn btn-outline-success">Edit</button>
                    <button type="button" class="btn btn-outline-warning">Delete</button>
                    </td>
                </tr> */}
            </tbody>
            </table>

        </div>
    )
    }
